import React, { useContext, useState } from 'react'
import api from '../../services/api';
import { GlobalContext } from '../../context/GlobalState';
import { useNavigate } from 'react-router';
import books from '../../assets/about-books.webp'
import Container from '@material-ui/core'

const LandingPage = () => {

    const [userCep, setUserCep] = useState('');
    const {viaCep, setViaCep} = useContext(GlobalContext)
    if (!viaCep) {
      throw new Error("Cep não encontrado.");
    }
    const navigate = useNavigate();

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      const numberCep = event.target.value.replace(/\D/g, '');
      setUserCep(numberCep);
    };

    async function getCEP() {
      if (userCep === '') {
        alert('Digite um CEP válido');
        setUserCep('');
        return;
      }
      try {
        const response = await api.get(`/${userCep}/json`);
        console.log(response.data);
        setViaCep(response.data);
        navigate('/form');
      } catch (error) {
        console.log('ERROR: ' + error);
        alert('Digite um CEP válido');
        setUserCep('');
        return;
      }
    }
  return (
    <Container>

      <input type="text" value={userCep} onChange={handleChange} />
      <button onClick={getCEP}>Buscar CEP</button>
      <img src={books} />
    </Container>
  )
}

export default LandingPage
