import React, { useContext, useState } from 'react';
import api from '../../services/api';
import { GlobalContext } from '../../context/GlobalState';
import Navbar from '../../components/Navbar/Navbar';
import { Container } from '@material-ui/core';
import useForm, { FormValues, FormErrors, FormTouched } from '../../customHooks/useForm';

interface ViaCep {
  bairro: string;
  cep: string;
  complemento: string;
  ddd: string;
  gia: string;
  ibge: string;
  localidade: string;
  logradouro: string;
  siafi: string;
  uf: string;
}

const Form: React.FC = () => {
  const { viaCep } = useContext(GlobalContext);
  const [userCep, setUserCep] = useState<ViaCep | null>(viaCep);
  if (!viaCep) {
    throw new Error('Cep não encontrado.');
  }

  const {
    values,
    errors,
    touched,
    handleChange,
    handleBlur,
    handleSubmit,
  } = useForm();

  const handleFormSubmit = (e: React.FormEvent<HTMLFormElement | HTMLInputElement>) => {
    e.preventDefault();
    if (Object.keys(errors).length === 0) {
      try {
        alert(
          `Foi enviado uma mensagem para o email cadastrado ${values.email} com as informações para dar prosseguimento ao cadastro.`
        );
      } catch (error) {
        alert('Não foi possível realizar o cadastro. Reveja as informações e tente novamente.');
      }
    } else {
      alert('Não foi possível realizar o cadastro. Reveja as informações e tente novamente.');
    }

    console.log(values);
  };

  const renderErrorMessage = (field: keyof FormErrors) => {
    if (touched[field] && errors[field]) {
      return <div className="error">{errors[field]}</div>;
    }
    return null;
  };

  return (
    <Container>
      <Navbar />
      <form onSubmit={handleFormSubmit}>
        <input
          name="name"
          placeholder="Nome Completo"
          value={values.name || ''}
          required
          maxLength={130}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {renderErrorMessage('name')}

        <input
          name="cpf"
          required
          maxLength={11}
          pattern="[0-9]{11}"
          placeholder="Cpf, deve conter apenas números"
          value={values.cpf || ''}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {renderErrorMessage('cpf')}

        <input
          name="email"
          placeholder="Email"
          required
          pattern="[a-zA-Z0-9._-]{1,64}@[a-zA-Z0-9.-]{1,}\.[a-zA-Z]{2,}"
          value={values.email || ''}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {renderErrorMessage('email')}

        <input
          name="cep"
          placeholder="Cep"
          pattern="[0-9]{5}-[0-9]{3}"
          required
          value={values.cep || userCep?.cep}
          onChange={handleChange}
          onBlur={handleBlur}
        />
        {renderErrorMessage('cep')}

                <input
                    name="neighborhood"
                    placeholder="Bairro"
                    required
                    value={values.neighborhood || userCep?.bairro}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />
                    {renderErrorMessage('neighborhood')}

                <input
                    name="city"
                    placeholder="Cidade"
                    required
                    value={values.city || userCep?.localidade}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />
                    {renderErrorMessage('city')}

                <input
                    name="street"
                    placeholder="Rua"
                    required
                    value={values.street || userCep?.logradouro}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />
                    {renderErrorMessage('street')}

                <input
                    name="complement"
                    placeholder="Complemento"
                    value={values.complement || ''}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />

                <input
                    name="state"
                    placeholder="Estado"
                    required
                    value={values.state || userCep?.uf}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />
                    {renderErrorMessage('state')}
                <input
                    name="number"
                    placeholder="Número"
                    required
                    value={values.number || ''}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    />
                    {renderErrorMessage('number')}

                <button>Enviar Cadastro</button>
            </form>
        </Container>
    );
};

export default Form;
