import {createGlobalStyle} from "styled-components"

export const GlobalStyle = createGlobalStyle `
:root{
    --background:#0F172A;
    --blue: #2563EB;
    --blue-gray:#1E293B;
    --text-title: #ffffff;
    --text-body:#94A3B8;
}
 *{
    margin:0;
    padding:0;
    box-sizing:border-box;
 }
html{
    @media(max-width: 1000px){
        font-size: 93.75%
    }
    @media(max-width: 720px){
        font-size: 87.05%
    }
}
 body{
    background: var(--background);
 }
 body, input, textarea, button{
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    color: var(--text-body);
 }
 h1,h2,h3,h4,h5,h6,strong{
    font-weight: 600;
    color: var(--text-title);
 }
 button{
    cursor:pointer;
    display: flex;
    border:none;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 16px;
    background: var(--blue);
    border-radius: 50px;
 }
 a{
    text-decotarion:none;
    cursor: pointer;
    color: var(--blue)
 }
[disable]{
    visable:0.6;
    cursor: not-allowed;
}
`
