import React, { useContext, useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from '../../services/api';
import { GlobalContext} from '../../context/GlobalState';

const Form = () => {
  const [userCep, setUserCep] = useState('');
  const globalContext = useContext(GlobalContext);

  if (!globalContext) {
    throw new Error("GlobalContext not found. Make sure the component is wrapped with GlobalStateProvider.");
  }

  const { viaCep, setViaCep } = globalContext;
  console.log("viaCep", viaCep);

  const history = useHistory();

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const numberCep = event.target.value.replace(/\D/g, '');
    setUserCep(numberCep);
  };

  async function getCEP() {
    if (userCep === '') {
      alert('Digite um CEP v치lido');
      setUserCep('');
      return;
    }
    try {
      const response = await api.get(`/${userCep}/json`);
      console.log(response.data);
      setViaCep(response.data);
      history.push('/catalog');
    } catch (error) {
      console.log('ERROR: ' + error);
      alert('Digite um CEP v치lido');
      setUserCep('');
      return;
    }
  }

  return (
    <div>
      <input type="text" value={userCep} onChange={handleChange} />
      <button onClick={getCEP}>Buscar CEP</button>
    </div>
  );
};

export default Form;
