import React, {useContext, useState} from 'react'
import api from '../../services/api';
import {GlobalContext} from '../../context/GlobalState';
import {useNavigate} from 'react-router';
import {books} from '../../assets/about-books.webp'
import {Container} from '@material-ui/core'
import {HorizontalFlex, ImgLanding, InputCep, LandingInfos} from './styles';

const LandingPage = () => {

    const [userCep,
        setUserCep] = useState('');
    const {viaCep, setViaCep} = useContext(GlobalContext)
    if (!viaCep) {
        throw new Error("Cep não encontrado.");
    }
    const navigate = useNavigate();

    const handleChange = (event : React.ChangeEvent < HTMLInputElement >) => {
        const numberCep = event
            .target
            .value
            .replace(/\D/g, '');
        setUserCep(numberCep);
    };

    async function getCEP() {
        if (userCep === '') {
            alert('Digite um CEP válido');
            setUserCep('');
            return;
        }
        try {
            const response = await api.get(`/${userCep}/json`);
            console.log(response.data);
            setViaCep(response.data);
            navigate('/form');
        } catch (error) {
            console.log('ERROR: ' + error);
            alert('Digite um CEP válido');
            setUserCep('');
            return;
        }
    }
    return (
        <Container>
            <HorizontalFlex>
                <LandingInfos>
                    <h1>Leiturama: Seu portal para uma jornada literária inesquecível"</h1>
                    <p>Aqui, a leitura ganha vida e você embarca em uma experiência única de
                        descoberta literária. Com nossa assinatura mensal, você receberá um livro
                        cuidadosamente selecionado de acordo com suas preferências diretamente em sua
                        porta.</p>

                    <InputCep>
                        <input type="text" value={userCep} onChange={handleChange}/>
                        <button onClick={getCEP}>Buscar CEP</button>
                    </InputCep>
                </LandingInfos>
                <ImgLanding src={books}/>
            </HorizontalFlex>
        </Container>
    )
}

export default LandingPage
